package w2;

public class Ex4_문자열 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String toolName = "JDK";
		// +의미는 연결연산자
		System.out.println(toolName + 1.8); //JDK1.8
		System.out.println("("+3+","+5+")"); // (3,5)
		System.out.println(toolName + "이 출시됨"); // JDK이 출시됨

	}

}
